import React, { Component } from 'react';
import { useState, useEffect } from "react";

import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";
import { getFirestore } from "firebase/firestore";
import { collection, addDoc, setDoc, getDocs, doc, onSnapshot } from "firebase/firestore"; 

import { pics } from './Pics';

const imgArray = [];
for (let i = 0; i < 10; i++) {
      imgArray.push('./assets/'+ i + '.jpg');
}  

const firebaseConfig = {
    apiKey: "AIzaSyA6LdJh0KDusKId6RiuzCY0wBtPcke1nyo",
    authDomain: "gametheory-1f44e.firebaseapp.com",
    projectId: "gametheory-1f44e",
    storageBucket: "gametheory-1f44e.appspot.com",
    messagingSenderId: "660278970969",
    appId: "1:660278970969:web:b49cd16f2140e53018ff48",
    measurementId: "G-Y68GG5ZMVV"
  };

  const app = initializeApp(firebaseConfig);
  const analytics = getAnalytics(app);
  const db = getFirestore(app);

function Form() {
    const [name, setName] = useState("");
    const [ig, setIg] = useState("");
    const [pic, setPic] = useState("");
    const [message, setMessage] = useState("");
    let [currentData, setCurrentData] = useState({ name: "", ig: "", pic: ""});
    let [regName, setRegName] = useState("");
    let [regIg, setRegIg] = useState("");
    let [regPic, setRegPic] = useState(0);
    let [subs, setSubs] = useState([]);

    useEffect(() => {
      // CURRENT DATA
      const unsub = onSnapshot(doc(db, "Response", "rGKZUj2fF5JpWiYZmCDJ"), (doc) => {
        console.log("Current data: ", doc.data());  
        setCurrentData = doc.data();  
        console.log('name', currentData.name);
      });
    });

    useEffect(() => {
      console.log('pic', pics[0]);
      getSubmission();
    }, []);

    const getSubmission = async() => {
      let allSubs = [];
      // Get all documents in a collection
      const querySnapshot = await getDocs(collection(db, "currResponse"));
      querySnapshot.forEach((doc) => {
      // console.log('Submitter Name:', doc.data().name);
      allSubs.push(doc.data());
      setSubs(allSubs);
      });
      let largest = subs[0].no;
      for(let i = 0; i < subs.length; i++){
        if(subs[i].no > largest){
          largest = subs[i].no;
        }
      }
      console.log('All Submission', subs[largest - 1]);

      // setRegName(subs[largest - 1].name);
      // setRegIg(subs[largest - 1].ig);
      // setRegPic(subs[largest - 1].pic);
      // console.log('reg name', regName.toString());
      // setRegName(allSubs[allSubs.length - 1].name);
      // setRegIg(allSubs[0].ig);
      // setRegPic(allSubs[0].pic);
    };

  //   const currSnapshot = onSnapshot(doc(db, "currResponse", "currUser"), (doc) => {
  //     setRegName(doc.data().name);
  //     setRegIg(doc.data().ig);
  //     setRegPic(doc.data().pic);
  // });

    let handleSubmit = async (e) => {
      e.preventDefault();
      const elementsArray = [...e.target.elements];
      console.log(elementsArray);

      try {
        let number = 1;
        let largest = subs[0].no;
        for(let i = 0; i < subs.length; i++){
          if(subs[i].no > largest){
            largest = subs[i].no;
          }
        }
        let nextNum = largest + 1;
        console.log('nextNum', nextNum);
        // const docRef = await addDoc(collection(db, "Response"), {
        //   no: number,
        //   name: name,
        //   ig: ig,
        //   pic: pic
        // });


        const data = {
          no: nextNum,
          name: name,
          ig: ig,
          pic: pic
        }


        const currRef = await setDoc(doc(db, "currResponse", data.no.toString()), data);


      } catch (e) {
        console.error("Error adding document: ", e);
      }



     try {
        let res = await fetch("https://httpbin.org/post", {
          method: "POST",
          body: JSON.stringify({
            name: name,
            ig: ig,
            pic: pic,
          }),
        });
        let resJson = await res.json();
        if (res.status === 200) {

          setMessage("You responded.");
          window.location.reload(false);

        } else {
          setMessage("Some error occured");
        }
      } catch (err) {
        console.log(err);
      }
    };

    return (
      <div className="App">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={name} id='name'
            placeholder="Name"
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="text" id='ig'
            value={ig}
            placeholder="Instagram"
            onChange={(e) => setIg(e.target.value)}
          />
          <input
            type="text" id="pic"
            value={pic}
            placeholder="Picture Choice"
            onChange={(e) => setPic(e.target.value)}
          />

          <button type="submit">Create</button>

          <div className="message">{message ? <p>{message}</p> : null}</div>
          <div><p>Current Name: {regName.toString()}</p></div>
          <div><p>Current IG: {regIg.toString()}</p></div>
          <div><p>Current Pic: {regPic.toString()}</p></div>
          <div><p>Result Pic: {parseInt(regPic) + 1}</p></div>
          <div><img src={pics[0]} width='100px' height='100px' /></div>


        </form>
      </div>
    );
  }

  export default Form;
