import { makeStyles } from '@material-ui/core';
import { Row, Col } from 'react-simple-flex-grid';
import "react-simple-flex-grid/lib/main.css";
import { allSubsInfo } from './utils/allSubmission';
import React, { useEffect, useState } from 'react';
import db from './utils/firebase.config';
import { collection, addDoc, setDoc, getDocs, doc, onSnapshot } from "firebase/firestore";
import Typography from '@material-ui/core/Typography'
import Tooltip from '@mui/material/Tooltip';
// import moment from 'moment';
// import { useMediaQuery } from 'react-responsive';
import useMediaQuery from '@mui/material/useMediaQuery';

const useStyles = makeStyles((theme) => ({
  container: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100vw',
    height: '100vh',
    margin: '0',
  },
  column: {
    border: '2px solid black',
    padding: '10%',
  },
  image: {
    width: '100%',
    height: '100%',
  },
  parentDesktop: {
    display: 'grid',
    [theme.breakpoints.down('md')]: {
      gridTemplateColumns: 'repeat(auto-fill, 10%)',
    },
    [theme.breakpoints.up('md')]: {
      gridTemplateColumns: 'repeat(auto-fill, 5%)',
    },
  },
  parentMobile: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, 10%)'
  },
  child: {
    border: '1px solid black',
    aspectRatio: '1/1'
  },
}));

export default function Content({ allSubmissions }) {
  // const isDesktopOrLaptop = useMediaQuery({
  //   query: '(min-width: 1224px)'
  // })
  // const isBigScreen = useMediaQuery({ query: '(min-width: 1824px)' })
  // const isTabletOrMobile = useMediaQuery({ query: '(max-width: 1224px)' })
  // const isPortrait = useMediaQuery({ query: '(orientation: portrait)' })
  // const isRetina = useMediaQuery({ query: '(min-resolution: 2dppx)' })

  const classes = useStyles();
  const picsOptions = ['https://imgur.com/ONukpTh.png', 'https://imgur.com/rY8d0Ye.png', 'https://imgur.com/xXAYEcH.png', 'https://imgur.com/aWS9TAH.png']
  const [allSubs, setAllSubs] = useState([])

  // Fetch all pre-existing images
  useEffect(() => {
    let responseArray = [];
    getDocs(collection(db, "Responses"))
      .then(response => {
        response.forEach((doc) => {
          responseArray.push(doc.data());
        });
      });

    setAllSubs(responseArray);
  }, []);

  console.log('res array content', allSubs);

  const [parentGridStyle, setParentGridStyle] = useState(classes.parentDesktop);

  // useEffect(()=>{
  //   if(isDesktopOrLaptop){
  //     setParentGridStyle(classes.parentDesktop); 
  //   }
  //   else if(isTabletOrMobile){
  //     setParentGridStyle(classes.parentMobile); 
  //   }
  // },[])

  return (
    <div className={classes.container}>
      <div className={classes.parentDesktop}>
        {allSubs.map(result =>
          //   <Col className={classes.column}
          //     xs={{ span: 6 }} sm={{ span: 5}} md={{ span: 1}}
          //     lg={{ span: 1 }} xl={{ span: 1 }}
          //   >{result.pic}
          //     <Tooltip title={
          //   <React.Fragment>
          //     <Typography color="inherit">Submitted by {result.name}</Typography>
          //     <em>at {result.time.toString()}</em>
          //   </React.Fragment>
          // } followCursor>
          //       <img className={classes.image} src={picsOptions[result.pic - 1]} />
          //     </Tooltip>
          //   </Col>
          <div className={classes.child}>
            <Tooltip title={
              <React.Fragment>
                <Typography color="inherit">Submitted by {result.name}</Typography>
                <em>at {result.time.toString()}</em>
              </React.Fragment>
            } followCursor>
              <img className={classes.image} src={picsOptions[result.pic - 1]} />
            </Tooltip>
          </div>
        )}
      </div>
    </div>
  );
}
