import logo from './logo.svg';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import Header from './components/Header';
import Footer from './components/Footer';
import Content from './components/Content';
import Form from './components/form';
import Background from './components/Background';
import Intro from './components/Intro';
import React, { Component, useState, useEffect, useRef } from 'react';
import db from './components/utils/firebase.config';
import { collection, addDoc, setDoc, getDocs, doc, onSnapshot } from "firebase/firestore";

let allSubs = [];

function App() {
  useEffect(() => {
    const getSubmission = getDocs(collection(db, "Responses"))
      .then(response => {
        response.forEach((doc) => {
          allSubs.push(doc.data());
        });
      });
      console.log('all', allSubs);
  }, [])


  return (
    <div className="App">
      <Background />
      <Content allSubmissions={allSubs} />
      <Intro allSubmissions={allSubs} />
      <Header />
      <Footer />
    </div>
  );
}

export default App;
