import React, { Component } from 'react';
import { useState, useEffect, useRef } from "react";
import Draggable from "react-draggable";
import { collection, addDoc, setDoc, getDocs, doc, onSnapshot } from "firebase/firestore";
import { makeStyles, withTheme } from '@material-ui/core';
import db from './utils/firebase.config';
import SubmitContext from './utils/submitContext';
import { pics } from './Pics';
import serverTimestamp from './utils/serverTimestamp';

const useStyles = makeStyles((theme) => ({
  container: {
    color: 'white',
  },
}));

const imgArray = [];
for (let i = 0; i < 10; i++) {
  imgArray.push('./assets/' + i + '.jpg');
}


function Form() {
  const classes = useStyles();

  const clickMenu = useRef(null);
  const form = useRef(null);

  const [submitted, setSubmitted] = useState(false);


  const [name, setName] = useState("");
  // const [ig, setIg] = useState("");
  const [pic, setPic] = useState("");
  const [message, setMessage] = useState("");
  let [currentData, setCurrentData] = useState({ name: "", pic: "" });
  let [regName, setRegName] = useState("");
  let [regIg, setRegIg] = useState("");
  let [regPic, setRegPic] = useState(0);
  let [subs, setSubs] = useState([]);
  let [ordered, setOrdered] = useState([]);
  let allSubs = [];
  let orderedPics = [];
  const picsOptions = ['https://imgur.com/ONukpTh.png', 'https://imgur.com/rY8d0Ye.png', 'https://imgur.com/xXAYEcH.png', 'https://imgur.com/aWS9TAH.png']

  useEffect(() => {
    // CURRENT DATA
    const unsub = onSnapshot(doc(db, "Response", "rGKZUj2fF5JpWiYZmCDJ"), (doc) => {
      console.log("Current data: ", doc.data());
      setCurrentData = doc.data();
      console.log('name', currentData.name);
    });
  });

  useEffect(() => {
    console.log('pic', pics[0]);
    getSubmission();
  }, []);

  const myData = [];

  const getSubmission = async () => {
    // Get all documents in a collection
    const querySnapshot = await getDocs(collection(db, "currResponse"));
    querySnapshot.forEach((doc) => {
      // console.log('Submitter Name:', doc.data().name);
      allSubs.push(doc.data());
    });
    setSubs(allSubs);

    let largest = allSubs[0].no;
    for (let i = 1; i < allSubs.length; i++) {
      if (allSubs[i].no > largest) {
        orderedPics.push(parseInt(allSubs[largest - 1].pic));
        largest = allSubs[i].no;
        // orderedSubs.push(largest);
      }
    }
    console.log('All Submission', allSubs[largest - 1].no);
    console.log('Ordered Submission', orderedPics);
    setOrdered(orderedPics);
    setRegName(allSubs[largest - 1].name);
    // setRegIg(allSubs[largest - 1].ig);
    // setRegPic(allSubs[largest - 1].pic);
    // console.log('reg name', regName.toString());
    // setRegName(allSubs[allSubs.length - 1].name);
    // setRegIg(allSubs[0].ig);
    // setRegPic(allSubs[0].pic);

  };
  for (let i = 0; i < ordered.length; i++) {
    myData.push({ key: i + 1, name: picsOptions[ordered[i]] })
  }

  console.log(' Submission Pics', myData);


  // the loop. it'll return array of react node.
  const children = myData.map((val) => (
    <div>
      <img src={val.name} width="50%"></img>
      <p> (Your text goes here. Also, you can place other elements within this parent div. </p>

      <button id={val.key}>{val.name}</button>
    </div>
  ));

  //   const currSnapshot = onSnapshot(doc(db, "currResponse", "currUser"), (doc) => {
  //     setRegName(doc.data().name);
  //     setRegIg(doc.data().ig);
  //     setRegPic(doc.data().pic);
  // });
  function answerPic(e) {
    if (e.target.id == "answer1") {
      setPic("1");
      document.getElementById('answer1button').style.backgroundColor = "black";
      document.getElementById('answer2button').style.backgroundColor = "white";

    }
    else if (e.target.id == "answer2") {
      setPic("2");
      document.getElementById('answer2button').style.backgroundColor = "black";
      document.getElementById('answer1button').style.backgroundColor = "white";

    }
    console.log('answer', e.target.id)
  }

  let handleSubmit = async (e) => {
    console.log('submiting');
    setSubmitted(true);
    e.preventDefault();
    const elementsArray = [...e.target.elements];
    console.log('elementsArray',elementsArray);

    try {
      let number = 1;
      let largest = subs[0].no;
      for (let i = 0; i < subs.length; i++) {
        if (subs[i].no > largest) {
          largest = subs[i].no;
        }
      }
      let nextNum = largest + 1;
      console.log('All Submission', largest);
      // const docRef = await addDoc(collection(db, "Response"), {
      //   no: number,
      //   name: name,
      //   ig: ig,
      //   pic: pic
      // });

      // const time = firebase.firestore
      // .Timestamp.now().toDate().toString();

      const data = {
        no: nextNum,
        name: name,
        // ig: ig,
        pic: pic,
        time: serverTimestamp.now(),
      }


      const currRef = await setDoc(doc(db, "currResponse", data.no.toString()), data);


    } catch (e) {
      console.error("Error adding document: ", e);
    }



    try {
      let res = await fetch("https://httpbin.org/post", {
        method: "POST",
        body: JSON.stringify({
          name: name,
          // ig: ig,
          pic: pic,
        }),
      });
      let resJson = await res.json();
      if (res.status === 200) {

        setMessage("You responded.");
        // window.location.reload(false);

      } else {
        setMessage("Some error occured");
      }
    } catch (err) {
      console.log(err);
    }
  };

  function showGame() {
    console.log('show game')
    clickMenu.current.style.display = "block";
  }
  function showReply() {
    console.log('show repl')
    form.current.style.display = "block";
  }
  return (
    <SubmitContext.Provider value={submitted}>
      <div className={classes.component}>
        <div className="menu" id="game" onClick={showGame}>
          <div className="flex">
            <div className="form"><form onSubmit={handleSubmit}>
              <input
                type="text"
                value={name} id='name'
                placeholder="Name"
                onChange={(e) => setName(e.target.value)}
              />

              <button type="submit">REPLY</button>

              <div className="message">{message ? <p>{message}</p> : null}</div>
              <div><p>Current Name: {regName.toString()}</p></div>
              {/* <div><p>Current IG: {regIg.toString()}</p></div> */}
              <div><p>Current Pic: {regPic.toString()}</p></div>
              <div><p>Result Pic: {parseInt(regPic) + 1}</p></div>
              <div><img src={pics[0]} width='100px' height='100px' /></div>
              <div>Choice of Answers:</div>
              <button id="answer1button" onClick={answerPic}><img id="answer1" src="https://imgur.com/b0ZYobY.png" width='100px' height='100px' /></button>
              <button id="answer2button" onClick={answerPic}><img id="answer2" src="https://imgur.com/ONukpTh.png" width='100px' height='100px' /></button>
              <div><p>Choosing Answer {pic}</p></div>
            </form>
            </div>
            <div className="column" id="col2">
              {children}
            </div>
          </div>
        </div>
      </div>
    </SubmitContext.Provider>
  );
}

export default Form;
