import { makeStyles } from '@material-ui/core';
import { useState, useEffect } from 'react';
import { red } from '@material-ui/core/colors';
import Draggable from "react-draggable";

const useStyles = makeStyles((theme) => ({
  container: {
    position: 'absolute',
    transform: 'translateX(-50%)',
    top: '-90vh',
    left: '50%',
    width: '45vw',
    height: '100vh',
    background: 'rgba(0,0,0, 0.5)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: '85px',
    backdropFilter: 'blur(10px)',
    textAlign: 'center',
    animation: "$hover 2s ease infinity"
  },
  hoverChange: {
    color: 'red',
  },
  content: {
    position: 'absolute',
    left: '50%',
    bottom: 0,
    textAlign: 'center',
    transform: 'translate(-50%, 0%)',
    color: 'white',
    fontSize: '28px',
    padding: '4%'
  },
  p: {
    marginBottom: '16px',
    fontSize: '14px',
  },
  "@keyframes hover": {
    "0%, 100%": {
      transform: 'translateY(0px)'
    },
    "50%": {
      transform: 'translateY(100px)'
    }
  }
}));

export default function Header() {
  const classes = useStyles();

  const [hoverClass, setHoverClass] = useState(null);

  const handleMouseHover = () => {
    setHoverClass(true);
    console.log('hover');
  }

  useEffect(() => {
    setHoverClass(hoverClass ? classes.hoverChange : '');
    console.log('change');
  }, [hoverClass]);

  return (    
      <div className={`${classes.container} ${hoverClass}`} onMouseEnter={handleMouseHover}>
        <div className={classes.content}>
          <div className={classes.p}>
            concept by ekezia + marco1ee.
          </div>
          <div className={classes.p}>
            player01 is ugh.isha, player02 is parano0dle.
          </div>

          <div className={classes.p}>
            this is a game where you are invited to play to cooperate or to betray.
          </div>
          <div className={classes.title}>
            game theory
          </div>
        </div>
      </div>
  );
}
