import React, { Component } from 'react';
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";
import { useState } from "react";

const firebaseConfig = {
    apiKey: "AIzaSyA6LdJh0KDusKId6RiuzCY0wBtPcke1nyo",
    authDomain: "gametheory-1f44e.firebaseapp.com",
    projectId: "gametheory-1f44e",
    storageBucket: "gametheory-1f44e.appspot.com",
    messagingSenderId: "660278970969",
    appId: "1:660278970969:web:b49cd16f2140e53018ff48",
    measurementId: "G-Y68GG5ZMVV"
  };

const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);

function Form() {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [mobileNumber, setMobileNumber] = useState("");
    const [message, setMessage] = useState("");

    let handleSubmit = async (e) => {
      e.preventDefault();
      try {
        let res = await fetch("https://httpbin.org/post", {
          method: "POST",
          body: JSON.stringify({
            name: name,
            email: email,
            mobileNumber: mobileNumber,
          }),
        });
        let resJson = await res.json();
        if (res.status === 200) {
          setName("");
          setEmail("");
          setMobileNumber("");
          setMessage("User created successfully");
        } else {
          setMessage("Some error occured");
        }
      } catch (err) {
        console.log(err);
      }
    };

    return (
      <div className="App">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={name} id='name'
            placeholder="Name"
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="text" id='ig'
            value={email}
            placeholder="Instagram"
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="text" id="pic"
            value={mobileNumber}
            placeholder="Picture Choice"
            onChange={(e) => setMobileNumber(e.target.value)}
          />

          <button type="submit">Create</button>

          <div className="message">{message ? <p>{message}</p> : null}</div>
        </form>
      </div>
    );
  }

  export default Form;
