import { makeStyles } from '@material-ui/core';
import "react-simple-flex-grid/lib/main.css";
import { useState, useContext, useEffect } from "react";
import SubmitContext from './utils/submitContext';
import getSubmission from './utils/allSubmission';

const useStyles = makeStyles((theme) => ({
  container: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100vw',
    height: '100vh',
    margin: '0',
    background: 'rgba(255,0.5)',
    backdropFilter: 'blur(15px)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    transition: 'all 2s ease'
  },
  removeContainer: {
    backdropFilter: 'blur(0px)',
    transition: 'all 2s ease'
  },
  image: {
    border: '5px solid black',
    position: 'absolute',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    visibility: 'visible',
    transition: 'all 1s ease',
  },
  removeImage: {
    border: '5px solid red',
    visibility: 'hidden',
    display: 'none',
    transition: 'all 1s ease'
  }
}));

export default function Intro() {
  const classes = useStyles();
  const submitted = useContext(SubmitContext);
  const [latestSubmission, setLatestSubmission] = useState(null);
  const [transitionContainer, setTransitionContainer] = useState(false);
  const [transitionImage, setTransitionImage] = useState(false);


  useEffect(() => {
    console.log('submit', submitted);
    console.log('allsub', getSubmission);
  }, []);

  const handleReveal = () => {
    setTransitionContainer(classes.removeContainer);
    setTransitionImage(classes.removeImage);
  }

  return (
    <div className={`${classes.container} ${transitionContainer}`} onClick={handleReveal}>
      <div className={`${classes.image} ${transitionImage}}}`}>AA</div>
        {/* <img className={`${classes.image} ${classes.removeImage}}`} id="answer1" src="https://imgur.com/b0ZYobY.png" width='500px' height='500px' /> */}
      Submitted: {submitted.toString()}
    </div>
  );
}
