import React, { Component } from 'react';
import { useState, useEffect, useRef } from "react";
import db from './firebase.config';
import { collection, addDoc, setDoc, getDocs, doc, onSnapshot } from "firebase/firestore";


let allSubs = [];
let orderedPics = [];
let orderedNames = [];
let myData = [];

const picsOptions = ['https://imgur.com/ONukpTh.png', 'https://imgur.com/rY8d0Ye.png', 'https://imgur.com/xXAYEcH.png', 'https://imgur.com/aWS9TAH.png']



const getSubmission = getDocs(collection(db, "currResponse"))
.then(response => {
  console.log('response', response);
      response.forEach((doc) => {
      console.log('Submitter Name:', doc.data().name);
      allSubs.push(doc.data());
  return myData;
});


let largest = allSubs[0].no;
for (let i = 1; i < allSubs.length; i++) {
  if (allSubs[i].no > largest) {
    orderedNames.push(allSubs[largest - 1].name);
    orderedPics.push(parseInt(allSubs[largest - 1].pic));

    largest = allSubs[i].no;
  }
}

for (let i = 0; i < orderedPics.length; i++) {
  myData.push({ key: i + 1, name: orderedNames[i], picture: picsOptions[orderedPics[i]] })
  console.log('my data', allSubs.length);
}
});

export const allSubsInfo = myData;
