let orderedPics = [];
let orderedNames = [];
let myData = [];

const picsOptions = ['https://imgur.com/ONukpTh.png', 'https://imgur.com/rY8d0Ye.png', 'https://imgur.com/xXAYEcH.png', 'https://imgur.com/aWS9TAH.png']



const getSubmission = getDocs(collection(db, "Responses"))
.then(response => {
      response.forEach((doc) => {
      console.log('Submitter Name:', doc.data().name);
      allSubs.push(doc.data());
});


console.log('how many', allSubs.length);

for (let i = 0; i < orderedPics.length; i++) {
