import { makeStyles } from '@material-ui/core';
import { useState, useEffect } from 'react';
import Draggable from "react-draggable";
import Form from './form';
import FollowCursorTooltips from './Tooltip';

const useStyles = makeStyles((theme) => ({
  container: {
    position: 'fixed',
    transform: 'translateX(-50%)',
    top: '90vh',
    left: '50%',
    width: '33vw',
    height: '100vh',
    background: 'rgba(0,0,0, 0.5)',
    borderRadius: '85px',
    backdropFilter: 'blur(10px)',
    textAlign: 'center',
    transition: 'all 2s ease',
  },
  hoverChange: {
    top: '20vh',
    transition: 'all 2s ease',
    animation: "$hover 2s ease infinity"
  },
  content: {
    position: 'absolute',
    left: '50%',
    top: 0,
    textAlign: 'center',
    transform: 'translate(-50%, 0%)',
    padding: '4%',
  },
  header: {
    color: 'white',
    fontSize: '28px',
  },
  form: {
    padding: '4%',
  }
}));

export default function Footer() {
  const classes = useStyles();

  const [hoverClass, setHoverClass] = useState(null);

  useEffect(() => {
    setHoverClass(hoverClass ? classes.hoverChange : '');
    console.log('change');
  }, [hoverClass]);

  return (
    <div className={classes.parent}>
      {/* <Draggable axis="y"> */}
      <div className={`${classes.container} ${hoverClass}`} onMouseEnter={(() => { setHoverClass(true) })} onMouseLeave={(() => { setHoverClass(false) })}>
        <div className={classes.content}>
          <FollowCursorTooltips>

            <div className={classes.header}>
              choose
            </div>
          </FollowCursorTooltips>
          <div className={classes.form}>
            <Form />
          </div>
        </div>
      </div>
      {/* </Tooltip> */}
      {/* </Draggable> */}
    </div>
  );
}
